def google(frase):
        frase = frase.replace('+', 'mais')
        headers = {'User-Agent': 'Mozilla/5.0 (Windows NT 10.0; Win64; x64) AppleWebKit/537.36 (KHTML, like Gecko) Chrome/64.0.3282.186 Safari/537.36'}
        r = requests.get('https://www.google.com/search?&q='+frase, headers=headers)
        soup = BeautifulSoup(r.text, 'html.parser')
        
        if 'quanto' in frase:
            return soup.find('div',{'class':'z7BZJb XSNERd'}).find('span',recursive=False).text
        
        elif 'tempo' in frase:
           return soup.find('span',{'class':'wob_t'}).text
       
        elif 'cambio' in frase:
            return soup.find('span',{'class':'DFlfde SwHCTb'}).text
        
        elif 'cotação' in frase:
             return soup.find('span',{'class':'IsqQVc NprOob XcVN5d'}).text
        else:
            print('nao encontrei oq vc procura')
                
